import React, { FunctionComponent, useState } from "react";
import Dish from "../models/dish";
import "./dish-card.css";
import Box from "@mui/material/Box";
import { color } from "@mui/system";
// typescript nous permet de controler l'entr√©e 'props' et assurer le bon typage
type Props = {
  dish: Dish;
};

const DishCard: FunctionComponent<Props> = ({ dish }) => {
  return (
    <Box sx={{ minWidth: 120 }} className="horizontal">
      {dish.name}

      <img className="rond" src={dish.picture} height="100%" width="100%" />
    </Box>
  );
};

export default DishCard;
