import React, { FunctionComponent, useState, useEffect } from "react";
import Dish from "./models/dish";
import DISH from "./models/data-local";

const App: FunctionComponent = () => {
  //règle n°1 : un hooks doit etre appeler à la racine d'un composant

  // initialisation de l'état du composant avec un tableau vide
  const [dish, setDish] = useState<Dish[]>([]);
  useEffect(() => {
    setDish(DISH);
  }, []); // le tableau vide en 2nd argument évite de déclancher le hook d'effet à chaque nouvel appel du composant

  return (
    <div>
      <h1>il y a {dish.length} plat dans la bdd!</h1>
    </div>
  );
};

export default App;
