import React, { FunctionComponent, useState } from "react";
import Dish from "../models/dish";
import "./dish-card.css";
import Box from "@mui/material/Box";
// typescript nous permet de controler l'entrée 'props' et assurer le bon typage
type Props = {
  dish: Dish;
};

const DishCard: FunctionComponent<Props> = ({ dish }) => {
  const [dishColor, setDishColor] = useState("green");
  return (
    <Box
      sx={{
        display: "flex",
        flexWrap: "wrap",
        minWidth: 300,
        backgroundColor: { dishColor },
      }}
    >
      <div className="horizontal">{dish.name} </div>
    </Box>
  );
};

export default DishCard;
